    private void guardarCandidatoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_guardarCandidatoActionPerformed

        String numeroDocumento = this.documentoCandi.getText();
        String nombreCandi = this.nombreCandidato.getText(); //capturo la información
        String tlfCandi = this.tlfCandidato.getText();
        String partidoPolitica = this.partidoPoli.getSelectedItem().toString();
        String ciudadCandi = this.ciudadCandidato.getSelectedItem().toString();
        String biografiaCandidato = this.biografiaCandi.getText();
        String smsCampaña = this.smsCampana.getText();
        String propuestaCandidato = this.propuestaCandi.getText();
        String contrasenaCandidato = String.valueOf(contrasenaCandi.getPassword());

        
        if (numeroDocumento.equals("")){
            JOptionPane.showMessageDialog(null, "Debe ingresar un numero de CC");
            return;
        }
        else if (!numeroDocumento.equals("")){

            if(numeroDocumento.length()<8||numeroDocumento.length()>10){
                JOptionPane.showMessageDialog(null, "La cedula debe de estar en rango de 8 a 10 digitos");
                return;
            }

            if(isNumeric(numeroDocumento) == false){
                JOptionPane.showMessageDialog(null, "Debes de ingresar solo numeros, sin letra o (.)");
                return;
            }
        }

        if (nombreCandi.equals("")){
            JOptionPane.showMessageDialog(null, "Debes ingresar nombre y apellido");
            return;
        }
        if(!nombreCandi.equals("")){
            String prueba  = nombreCandi.replaceAll("\\s+","");

            //JOptionPane.showMessageDialog(null, prueba);
            if (!prueba.matches("[a-zA-Z]+") == true){
                //JOptionPane.showMessageDialog(null, "tiene caracteres y numero");
                JOptionPane.showMessageDialog(null, "ingrese el nombre si caracteres especiales");
                return;
            }
        }

        if (tlfCandi.equals("")){
            JOptionPane.showMessageDialog(null, "Debede ingresar un número teléfonico");
            return;
        }

        if (isNumeric(tlfCandi) == false) {
            JOptionPane.showMessageDialog(null, "El numero de teléfono no debe contener caracteres especiales");
            return;
        }

        else if (tlfCandi.length()<10||tlfCandi.length()>10) {
            JOptionPane.showMessageDialog(null, "El numero de teléfono debe de contener 10 digitos con el código");
            return;
        }

        if (partidoPolitica.equals("seleccione una opción")){
            JOptionPane.showMessageDialog(null, "Elija un partido politico");
            return;
        }

        if (ciudadCandi.equals("seleccione una opción")){
            JOptionPane.showMessageDialog(null, "Seleccione una opción para la ciudad");
            return;
        }
        
        if (biografiaCandidato.equals("")){
            JOptionPane.showMessageDialog(null, "Debes ingresar una pequeña biografia");
            return;
        }
        
       if (smsCampaña.equals("")){
           JOptionPane.showMessageDialog(null, "Debe de crear un mensaje de campaña");
             return;
        }
       
        if (propuestaCandidato.equals("")){
            JOptionPane.showMessageDialog(null, "Debe de crear una propuesta");
            return;
        }
        
        if (propuestaCandidato.equals("")){
            JOptionPane.showMessageDialog(null, "Debe de crear una propuesta");
            return;
        }
        
        if (contrasenaCandidato.equals("")){
            JOptionPane.showMessageDialog(null, "Debe de crear una contraseña");
            return;
        }
        

        else {
            ClsCandidato candidato = new ClsCandidato(numeroDocumento, nombreCandi,
            tlfCandi, partidoPolitica, ciudadCandi, biografiaCandidato, smsCampaña, propuestaCandidato);

            ClsMensaje mensaje = this.controladorcandidato.agregarCandidato(candidato);
            if (mensaje.getTipo().equals(ClsMensaje.OK)) { //si todo el proceso es correcto lo que hace es agregarme la info
                llamarcandidato();
            }

            JOptionPane.showMessageDialog(rootPane, mensaje.getTexto());
            
            }
        
        this.clear();
        
    }//GEN-LAST:event_guardarCandidatoActionPerformed

            private void clear(){
        this.documentoCandi.setText("");
        this.nombreCandidato.setText("");
        this.tlfCandidato.setText("");
        this.partidoPoli.setSelectedIndex(0);
        this.ciudadCandidato.setSelectedIndex(0);
        this.biografiaCandi.setText("");
        this.smsCampana.setText("");
        this.propuestaCandi.setText("");
        this.contrasenaCandi.setText("");
        
    }
    
    
